import {useState} from 'react'

export default function IntrestForm(props){
    const [currentSavings, setCurrentSavings] = useState("");
    const [YearlySavings, setYearlySavings] = useState("");
    const [expectedInterest, setExpectedInterest] = useState("");
    const [investmentDuration, setInvestmentDuration] = useState("");
    
    function formSubmition(event, id){
        if(id === "current-savings"){setCurrentSavings(event.target.value)}
        else if(id === "yearly-contribution"){setYearlySavings(event.target.value)}
        else if(id === "expected-return"){setExpectedInterest(event.target.value)}
        else if(id === "duration"){setInvestmentDuration(event.target.value)}      
    }
    
    
    function onSubmission(event){
        event.preventDefault();
        const submittedData = {
            currentSavings: +currentSavings,
            YearlySavings: +YearlySavings,
            expectedInterest: +expectedInterest,
            investmentDuration: +investmentDuration
        }
        console.log(submittedData);
        // props.onGetData(submittedData);
    }

    return (
        <form className="form" onSubmit={onSubmission}>
        <div className="input-group">
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input type="number" id="current-savings"  onChange={(event) => formSubmition(event, "current-savings")}/>
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input type="number" id="yearly-contribution"  onChange={(event) => formSubmition(event, "yearly-contribution")}/>
          </p>
        </div>
        <div className="input-group">
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input type="number" id="expected-return"  onChange={(event) => formSubmition(event, "expected-return")}/>
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input type="number" id="duration"  onChange={(event) => formSubmition(event, "duration")}/>
          </p>
        </div>
        <p className="actions">
          <button type="reset" className="buttonAlt">
            Reset
          </button>
          <button type="submit" className="button">
            Calculate
          </button>
        </p>
      </form>
    );
}
